func max(a, b int) int {
	if a > b {
		return a
	}
	return b
}

func findMinInMap(m map[string]int) (string, int) {
	if m["T"] < m["F"] {
		return "T", m["T"]
	}
	return "F", m["F"]
}

func maxConsecutiveAnswers(answerKey string, k int) int {
	longest := 0
	l, r := 0, 0

	start := time.Now()

	m := make(map[string]int)
	m["T"] = 0
	m["F"] = 0

	for r < len(answerKey) {
		if longest == len(answerKey) {
			return longest
		}

		val, ok := m[string(answerKey[r])]
		if ok {
			val++
			m[string(answerKey[r])] = val
		}

		_, minVal := findMinInMap(m)
		if minVal < k {
			r++
		} else {
			l++
			val = m[string(answerKey[l])] //val = answerKey[l]
			val--
			m[string(answerKey[l])] = val
		}

		currLen := 0
		for _, elem := range m {
			currLen += elem
		}
		longest = max(longest, currLen)
	}
	fmt.Println(time.Since(start))
	return longest
}
